export type BOOKING_STATUSES_UNION = 'CANCELLED' | 'CONFIRMED' | 'REDEEMED'
export type AVAILABILITY_STATUSES_UNION = 'AVAILABLE' | 'CLOSED' | 'SOLD_OUT' | 'FREESALE'

export const BOOKING_STATUSES: {[key in BOOKING_STATUSES_UNION]: BOOKING_STATUSES_UNION}
export const AVAILABILITY_STATUSES: {[key in AVAILABILITY_STATUSES_UNION]: AVAILABILITY_STATUSES_UNION}
